import React, { Component } from 'react';
import LessonCard from './lessonCard';

import { lessons } from 'assets/data/mockup_lessons';
import { levels } from 'assets/data/mockup_levels';
import { Row, Col } from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import 'font-awesome/css/font-awesome.min.css';
import 'assets/css/style.css';

class Education extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            sampleData: lessons
         }
    }
    displayLessonsByLevels(){
        
    }

    displayLessons() {
        let list = [];
        this.state.sampleData.map((item, index) => {
            list.push(
                <div id={item.id}>
                    <LessonCard video= {item}/>
                </div>
            );
        })

        this.state.filteredVideos.filter((video) => video.level === level).map((video, index) => {
            videoLists.push(
                <Col xs={3} key={index} id={video.id}>
                    <div className={style.videoContainer} onClick={() => this.videoComponentClickHandler()}>
                        <video width="320" height="200" poster={video.poster} controls preload="none">
                            <source src={video.path} type="video/mp4" ></source>
                        </video>
                        <div className={style.descContainer}>
                            <div className={style.title}><b>{video.title}</b></div>
                            <div className={style.view}>View: {video.viewCount}</div>
                            <PopupDetail videoInfo={video} />
                        </div>
                    </div>
                </Col>
            );
        })

        return list;
    }

    render() { 
        return (
        <div>
            <div className="header">
                    Lesson Plan
                    <i className="score fa fa-star">&nbsp;36</i>
                <FontAwesomeIcon icon="check-square" />
            </div>
            <div className="content">
                {this.displayLessons()}
            </div>
        </div>
        );
    }
}
 
export default Education;
