import React, { Component } from 'react';
import StarRatingComponent from 'react-star-rating-component';
import './LessonCard.css';

import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button} from 'reactstrap';
import { useHistory } from 'react-router-dom';

import styled from 'styled-components'

class LessonCard extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }


  viewLesson = () => {
    const history = useHistory;
    console.log(this.props.video.id)
    history.push({
      pathname: '/education',
      data: this.props.video.id // your data array of objects
      // `/education/${this.props.video.id}`
    })

  }

    render() { 
      let progress = this.props.video.progress;
        return (
          // <Card>
  
          <Card className="lesson" onClick={() => this.viewLesson()}>
            {/* <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" /> */}
            <CardBody>
              <CardTitle>{this.props.video.title}</CardTitle>
              <CardSubtitle>{this.props.video.description}</CardSubtitle>
              <StarRatingComponent name="rating"  starCount={5}  value={this.props.video.rating} /> <br/>
              {this.props.video.progress !== 0 &&
                  <progress id="file" value={this.props.video.progress} max="100"> </progress>
                }
            </CardBody>
          </Card>
        );
    }
}
 
export default LessonCard;
