import React, { Component } from 'react';
import { useHistory } from "react-router-dom";
import * as src from "assets/images/lessons/accounting.jpg";
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
} from 'reactstrap';

class LessonPage extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }

    async componentDidMount() {
        console.log(this.props)
        // if (!this.props.video.id)
        //     this.props.history.push(`/education`)
    }

    render() { 
        return (
            <div>
                <video width="100%" height="auto" poster={src} controls preload="none">
                    <source src="https://www.youtube.com/watch?v=wvXDB9dMdEo" type="video/mp4"></source>
                </video>
                <h2>Introdction to finance</h2>
                <Card className="lesson" onClick={() => this.viewLesson()}>
                    <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" />
                    <CardBody>
                        <CardTitle>{this.props.video.title}</CardTitle>
                        <CardSubtitle>{this.props.video.description}</CardSubtitle>
                        <StarRatingComponent name="rating" starCount={5} value={this.props.video.rating} /> <br />
                        {this.props.video.progress !== 0 &&
                            <progress id="file" value={this.props.video.progress} max="100"> </progress>
                        }
                    </CardBody>
                </Card>

            </div>
         );
    }
}
 
export default LessonPage;
